import React from "react";
import { MashingaContainer } from "./styles";
import Body from "./components/Body";

function Mashinga(props) {
  React.useEffect(() => {
    let localToken = localStorage.getItem("token");
    SecureService.verifyToken(localToken)
      .then((retrivedUser) => {
          setUserDetails(retrivedUser)(dispatch);
          setLocalTokenStatus(true);
      })
      .catch((err) => {
        if (
          window.location.pathname.includes(basePath) &&
          window.location.pathname !== basePath + "login"
        ) {
          history.push(basePath + "login?redirect=" + window.location.pathname);
        }
        setLocalTokenStatus(true);
      });
  }, []);
  return <MashingaContainer>
    <Body/>
  </MashingaContainer>;
}
export default Mashinga;
