import React from "react";
import { BodyContainer } from "./styles";
import { Route, Switch } from "react-router-dom";
import { appBasePath } from "../../config/paths";
import Splash from "../pages/Splash";
import { Context } from "../../Store";
import { getUser } from "../../Store/user/accessors";

function Body(props) {
  const  {state} = useContext(Context);

  if(getUser(state).role === "user"){
    return <BodyContainer>
    <Switch>
      <Route path={appBasePath} component={Splash}/>
      <Route path={appBasePath+"/login"} component={Splash}/>
      <Route path={appBasePath+"/signup"} component={Splash}/>
      <Route path={appBasePath+"/transver"} component={Splash}/>
      <Route path={appBasePath+"/profile"} component={Splash}/>
      <Route path={appBasePath+"/settings"} component={Splash}/>
    </Switch>
  </BodyContainer>;
  }
  else if(getUser(state).role === "admin"){
    return <BodyContainer>
    <Switch>
      <Route path={appBasePath} component={Splash}/>
      <Route path={appBasePath+"/login"} component={Splash}/>
      <Route path={appBasePath+"/profile"} component={Splash}/>
      <Route path={appBasePath+"/settings"} component={Splash}/>
    </Switch>
  </BodyContainer>;
  }
  else {
      return <BodyContainer>
      <Switch>
        <Route path={appBasePath} component={Splash}/>
        <Route path={appBasePath+"/login"} component={Splash}/>
        <Route path={appBasePath+"/signup"} component={Splash}/>
        <Route path={appBasePath+"/transver"} component={Splash}/>
        <Route path={appBasePath+"/profile"} component={Splash}/>
        <Route path={appBasePath+"/settings"} component={Splash}/>
      </Switch>
    </BodyContainer>;

  }

}
export default Body;
