import createReducer from "../common/createReducer";
import { getTransformFunctions, fallBackRates } from "../../utils/transformers";

var reducers = {
  SET_USER_DETAILS: (state, action) => {
    return action.userDetails;
  },
  UPDATE_USER_PREFERENCE:(state,action)=>{
    return {
      ...state,
      preference:{
        ...state.preference,
        ...action.updates
      }
    }
  },
  UPDATE_TRASNFORMERS:(state,action)=>{
    return {
      ...state,
      view:action.transformers
    }
  },
  LOGOUT: (state, action) => {
    document.cookie = "";
    localStorage.removeItem("token");
    return {
      role: "unauthorised",
    };
  },
};

const initialState = {
  role: "unauthorised",
  ...getTransformFunctions(fallBackRates,{currency:"USD",units:"Metric"})
};

export default createReducer(initialState, reducers);
