import styled from "styled-components";

export const SplashContainer = styled.div`
    display:flex;
    align-items:center;
    justify-content:center;
    flex-direction:column;
    height:100vh;
`;

export const AppLogo = styled.img`
    height:80px;
`;
export const AppTitle = styled.div`
    margin-top:50px;
    font-size:24px;
`;
